mapper.update:

UPDATE	tb_board
SET		hit_cnt = NVL(hit_cnt, 0)+1
WHERE	idx=#{idx}

mapper.select:

SELECT	idx, hit_cnt, crea_id, crea_dtm, title, contents
FROM	tb_board
WHERE	idx=#{idx}

mapper.update:

UPDATE	tb_board
SET		title=#{title}
		contents=#{contents}
WHERE	idx=#{idx}


UPDATE	tb_board
SET		del_gb = 'Y'
WHERE	idx=#{idx}
